import { createReducer } from '@reduxjs/toolkit'
import { updateFood } from './action'

let initState = {
    menu: [],
    itemToEdit: {},
    ingredients: [],
    categories: []
}


const menuReducer = createReducer(
    initState,
    (builder) => {
        builder.addCase(updateMenu, (state, action) => {
            state.menu = action.payload
        })
    }
)

export default menuReducer
